SELECT
	CP.COD_PRODUTO
	,CP.NO_DEPARTAMENTO
	,CP.NO_SECAO
	,CP.NO_GRUPO
	,CP.NO_SUBGRUPO
	,CP.DESCRICAO
	,bi.dbo.fn_FormataVlr_Excel(AVG(LP.VLR_VENDA)) AS AVG_PV
	,bi.dbo.fn_FormataVlr_Excel(C.VLR_EMB_COMPRA) as VLR_EMB_COMPRA
	,bi.dbo.fn_FormataVlr_Excel(C.QTD_EMB_COMPRA) as QTD_EMB_COMPRA
FROM
	BI.dbo.BI_CAD_PRODUTO AS CP
	left JOIN BI_LINHA_PRODUTOS AS LP
		ON CP.COD_PRODUTO = LP.COD_PRODUTO
	LEFT JOIN BI.dbo.VW_CUSTOS_ATIVOS AS C
		ON 1=1
		AND CP.COD_PRODUTO = C.COD_PRODUTO
		AND CP.COD_FORNECEDOR = C.COD_FORNECEDOR
WHERE 1=1
	AND CP.COD_PRODUTO IN 
	--(64743,64750,106498,116749,119658,143103,167345,194235,194273,202329,222358,230049,230056,232654,250627,253932,266369,300667,306287,310161,341745,341752,342049,342100,346948,346979,364829,364843,397384,423373,423434,451475,451802,452434,480628,487122,487139,487146,487153,487160,517416,517447,523233,524728,524735,524766,535700,552547,560986,561044,575232,575249,575294,575300,575317,575331,575393,575423,628877,643221,880688,918138,980470,984270,991752,991753,991757,991758,998368,998370,998863,998864,998869,998870,998871,998872,998899,1015652,1015653,1015654,1015655,1015656,1015657,1015714,1015715,1015716,1015717,1015718,1015721,1015722,1015723,1015724,1015725,1015727,1015728)
	(942,1420,1677,1678,1706,1708,1717,1718,1719,1720,1723,1724,1725,1726,1727,1759,1760,2253,2529,2531,2536,3049,3053,3054,3456,3457,4259,4269,4709,4714,4717,13072,13073,13294,20345,20346,20348,21129,21343,21705,21707,22027,22028,22030,22032,22035,22037,22048,22049,22780,23292,23294,23295,23299,23300,23301,23302,23305,23308,23486,23488,30147,31014,31523,31623,32891,33275,33276,33703,36338,36339,36340,36341,36344,37260,37261,37262,38235,39630,39631,40050,40053,40061,40066,40068,40070,40074,40075,40076,40077,40078,40079,40080,40081,40082,40170,40321,40328,40330,40362,40416,40600,41756,41758,43998,45056,45738,45739,45740,47111,47791,47792,47793,47794,48284,48882,48889,48987,49166,51459,51461,53102,53912,56149,56150,56152,56153,62137,62314,62316,63240,63241,63245,65664,66032,66099,67230,68820,68822,68823,70550,70873,73955,75855,75860,75971,76330,76332,76337,76577,76579,76580,80128)
