	CP.COD_PRODUTO
	,CP.NO_DEPARTAMENTO
	,CP.NO_SECAO
	,CP.NO_GRUPO
	,CP.NO_SUBGRUPO
	,CP.DESCRICAO
	,C.VLR_EMB_COMPRA
	,C.QTD_EMB_COMPRA
order by cp.COD_PRODUTO