SELECT --TOP 10
	CONVERT(DATE,VP.DATA) AS DATA
	,VP.COD_LOJA
	,VP.COD_PRODUTO
	,BI.dbo.fn_FormataVlr_Excel(VP.VALOR_TOTAL) AS VLR_VENDA
	,BI.dbo.fn_FormataVlr_Excel(VP.QTDE_PRODUTO) AS QTD_VENDA
	,BI.dbo.fn_FormataVlr_Excel(PV.VALOR) AS PRECO_VENDA
FROM
	BI.DBO.BI_VENDA_PRODUTO AS VP
	LEFT JOIN [BI].[DBO].[BI_PRECO_VENDA] AS PV
	ON 1=1
		AND VP.COD_PRODUTO = PV.COD_PRODUTO
		AND VP.COD_LOJA = PV.COD_LOJA
		AND CONVERT(DATE,PV.DTA_INI) = (SELECT MAX(TPV.DTA_INI) FROM [BI].[DBO].[BI_PRECO_VENDA] AS TPV WHERE TPV.TIPO = 0 AND TPV.COD_LOJA = PV.COD_LOJA AND TPV.COD_PRODUTO = PV.COD_PRODUTO AND CONVERT(DATE,TPV.DTA_INI) <= CONVERT(DATE,VP.DATA) AND CONVERT(DATE,TPV.DTA_FIM) >= CONVERT(DATE,VP.DATA) )
		AND CONVERT(DATE,PV.DTA_FIM) >= CONVERT(DATE,VP.DATA)
		AND PV.TIPO = 0		
WHERE 1=1
	AND CONVERT(DATE,VP.DATA) BETWEEN CONVERT(DATE,'20140101') AND CONVERT(DATE,'20141031')
	AND VP.COD_LOJA IN (2,3)
	--AND VP.COD_PRODUTO IN (709507)
	AND VP.COD_PRODUTO IN (709507,475709,317863,68949,612609,271356,945356,115421,991509,991511,991510,83126,13482,10085,2363,91657,1014388,1014391,1014184,18951,6156,17633,993268,993270,1014101,1014100,1013372,1013373,1013375,1013374,505529,505567,283144,492416,746083,158725,63364,63388,418836,941587,515795,289801,247313,206976,995255,1000161,1000157,1001180,669368,669351,669115,66631,130899,105118,668163,74254,552424,1004093,1004092,1004091,1000799,1000800,1000801,407625,669887,1013794,1013792,1013793,14915,14922,28943,12751,276184,2097,1014902,1014901,1014900,1007714,452847,452823,452854,60431,201384,240413,452830,575546,513807,575553,995257,128674,512701,512664,512572,512541,992691,983464,1002547,1002568,1001573,520300,523134,990838,554619,554602,1007707,1007708,1007716,1007717,1007718,1007719,1007720,1007721,1007709,1007710,1007711,1007712,1007713,1007684,1007691,1007692,1007700,1007685,1007686,1007701,1007702,1007703,1007704,1007705,1007706,1003204,1003201,1010057,1010058,1010059,991858,991879,339933,362856,339926,716765,1001807,1001333,106849,106894,242097,358132,609111,948777,292986,709125,920599,1014098,85076,998970,1014255,546904,120999,429696,429702,428996,1013834,1013840,1013839,1013842,1013836,1013835,1013841)
ORDER BY
	CONVERT(DATE,VP.DATA)
	,VP.COD_LOJA
	,VP.COD_PRODUTO	
