DECLARE @TAB_CUSTO_FORN AS TABLE
(
	COD_LOJA INT
	,COD_PRODUTO INT
	,COD_FORNECEDOR INT
	,VAL_CUSTO_REP NUMERIC(18,2)
	,VAL_CUSTO_EMBALAGEM NUMERIC(18,2)
	,QTD_EMBALAGEM_COMPRA NUMERIC(18,2)
)

INSERT INTO @TAB_CUSTO_FORN
	SELECT --TOP 10
		PF.COD_LOJA
		,PF.COD_PRODUTO
		,PF.COD_FORNECEDOR
		,PL.VAL_CUSTO_REP
		,PF.VAL_CUSTO_EMBALAGEM
		,PF.QTD_EMBALAGEM_COMPRA
	FROM
		[192.168.0.6].ZEUS_RTG.DBO.TAB_PRODUTO_FORNECEDOR AS PF
		INNER JOIN [192.168.0.6].ZEUS_RTG.DBO.TAB_PRODUTO_LOJA AS PL
			ON 1=1
			AND PF.COD_LOJA = PL.COD_LOJA
			AND PF.COD_PRODUTO = PL.COD_PRODUTO
	WHERE 1=1
		AND PF.COD_PRODUTO IN
		(66631,66754,174565,648219,1002788,54706,250627,1002457,1002545,992518,143431,1002791,1002409,151658,1002772,1002790,1002543,1002413,1002774,1002406,1002415,1002416,1002418,657440,657440,994867,523295,105941,486798,993610,1007702,325653,995198,1002422,1016116,1015686,592680,1002421,1002483,1011404,561570,1007702,1002482,1002420,1014184,64743,309271,1010054,112901,1004061,1004057,561587,1007692,552349,552134,1000124,1016280,315838,552325,106948,106849,1008648,561587,933230,145183,552080,561594,920520,552332,1002472,554633,1011395,1011406,1004090,1002036,53693,1010050,545419,992604,1014782,429092,1002480,994724,561617,1000796,751476,998483,1016113,512664,306287,990815,1002481,993339,643238,520300,638883,1004063,1002405,552356,471541,1915,53686,53686,1000795,1004058,552370,480628)

SELECT
	T.COD_LOJA
	,T.COD_PRODUTO
	,T.COD_FORNECEDOR
	,BI.dbo.fn_FormataVlr_Excel(T.VAL_CUSTO_REP) AS VAL_CUSTO_REP
	,BI.dbo.fn_FormataVlr_Excel(T.VAL_CUSTO_EMBALAGEM) AS VAL_CUSTO_EMBALAGEM
	,BI.dbo.fn_FormataVlr_Excel(T.QTD_EMBALAGEM_COMPRA) AS QTD_EMBALAGEM_COMPRA
	,CP.COD_NCM
FROM
	BI_CAD_PRODUTO AS CP
	INNER JOIN @TAB_CUSTO_FORN AS T
		ON 1=1
		AND CP.COD_PRODUTO = T.COD_PRODUTO
		AND CP.COD_FORNECEDOR = T.COD_FORNECEDOR
WHERE 1=1
	AND CP.COD_PRODUTO IN
	(66631,66754,174565,648219,1002788,54706,250627,1002457,1002545,992518,143431,1002791,1002409,151658,1002772,1002790,1002543,1002413,1002774,1002406,1002415,1002416,1002418,657440,657440,994867,523295,105941,486798,993610,1007702,325653,995198,1002422,1016116,1015686,592680,1002421,1002483,1011404,561570,1007702,1002482,1002420,1014184,64743,309271,1010054,112901,1004061,1004057,561587,1007692,552349,552134,1000124,1016280,315838,552325,106948,106849,1008648,561587,933230,145183,552080,561594,920520,552332,1002472,554633,1011395,1011406,1004090,1002036,53693,1010050,545419,992604,1014782,429092,1002480,994724,561617,1000796,751476,998483,1016113,512664,306287,990815,1002481,993339,643238,520300,638883,1004063,1002405,552356,471541,1915,53686,53686,1000795,1004058,552370,480628)