SELECT
	VP.COD_LOJA
	,CONVERT(DATE,VP.DATA) AS DATA
	,(CASE WHEN CONVERT(DATE,DATA) BETWEEN CONVERT(DATE,'2015-11-20') AND CONVERT(DATE,'2015-11-22')THEN 'P1' ELSE 'P2' END) AS PERIODO
	,VP.COD_PRODUTO
	,CP.DESCRICAO AS NO_PRODUTO
	,CP.NO_DEPARTAMENTO
	,CP.NO_SECAO
	,CP.NO_GRUPO
	,LP.FORA_LINHA
	,BI.DBO.FN_FORMATAVLR_EXCEL(SUM(VP.VALOR_TOTAL)) AS VLR_TOTAL
	,BI.DBO.FN_FORMATAVLR_EXCEL(SUM(VP.QTDE_PRODUTO)) AS QTD_TOTAL
FROM
	BI.dbo.BI_VENDA_PRODUTO AS VP
	INNER JOIN BI.dbo.BI_CAD_PRODUTO AS CP
		ON VP.COD_PRODUTO = CP.COD_PRODUTO	
	LEFT JOIN BI.DBO.BI_LINHA_PRODUTOS AS LP
		ON 1=1kk
		AND VP.COD_PRODUTO = LP.COD_PRODUTO
		AND VP.COD_LOJA = LP.COD_LOJA
WHERE 1=1
	AND CP.COD_PRODUTO IN (991006,991008,1012686,1018944,1001711,1015601,1002476,1002487,1002477,1002474,1002473,1002470,1002468,1002472,1014736,586948,800457,1017574,1017575,73523,1028294,1028295,197694,120562,750400,192699,78825,78924,1014686,534819,1027121,1027122,1012721,1012716,535144,534871,635707,1012717,1012719,689700,1012720,1023824,491327,1023827,1023829,491334,991661,1025224,1025225,1015030,1016268,991756,56359,288705,911566,911573,527729,995044,1025754,202053,286503,182782,1015028,59138,928311,125055,981705,64224,56342,64231,1014085,1014087,1014086,504270,1010573,1010574,504102,504287,1010575,991852,991861,1002434,1002446,1002443,1002437,1002453,1002435,1002433,1002459,1002436,1002455,1002448,1002444,570756,288408,995266,995267,1018029,1017181,590532,1014352,1027451,1017182,1014350,994378,992820,1000837,1014349,1014351,1020431,1014353,590525,1027455,1015341,120098,216494,120074,121736,1016276,1016245,1013735,1013734,992692,992693,992691,184625,1002442,1002438,1002463,1002440,1022378,550918,450171,467445,566735,1028296,1028297,19446,316316,262279,28585,262286,129282,1023838,1023839,558952,1015859,506311,1027764,1027765,1027766,1027767,930826,61117,563710,563550,563840,428828,468169,1015651,1012837,1017659,1012838,1017660,1012839,1000509,1000506,1004202,1004159,1000502,1000503,1004158,1004204,1001106,1013257,1013204,1013205,1016620,1016618,504492,638890,523295,201551,638913,751476,751469,991713,995746,1028491,1028493,1028492,1001994,1002047,277075,108874,627672,941594)
	AND CONVERT(DATE,DATA) BETWEEN CONVERT(DATE,'2015-11-27') AND CONVERT(DATE,'2015-11-29')
	--AND CONVERT(DATE,DATA) BETWEEN CONVERT(DATE,'2015-11-20') AND CONVERT(DATE,'2015-11-22')
GROUP BY
	VP.COD_LOJA
	,VP.DATA
	,(CASE WHEN CONVERT(DATE,DATA) BETWEEN CONVERT(DATE,'2015-11-20') AND CONVERT(DATE,'2015-11-22')THEN 'P1' ELSE 'P2' END)
	,VP.COD_PRODUTO
	,CP.DESCRICAO
	,CP.NO_DEPARTAMENTO
	,CP.NO_SECAO
	,CP.NO_GRUPO
	,LP.FORA_LINHA
ORDER BY
	VP.COD_LOJA
	,CP.NO_DEPARTAMENTO
	,SUM(VP.VALOR_TOTAL) DESC